/** * @param JoinPointInterface $joinPoint * @return void * @Flow\AfterReturning("method(Acme\Demo\Domain\Repository\.*Repository->(add|update|remove)())") */public function repositoryActivityAdvice(JoinPointInterface $joinPoint) {  $object = $joinPoint->getMethodArgument('object');  $message = sprintf('%s() called for object of type %s',    $joinPoint->getMethodName(),    get_class($object)    );  $this->systemLogger->log($message);}