public function getDetailData($sLocale, $iParentPathId, $bIgnoreMissing = false)    {        $_sKey = $this->generateDetailDataKey($sLocale, $iParentPathId, $bIgnoreMissing);        $_aMapLinks = array();        try        {            $_aEntries = $this->getStore()->range($_sKey, 0, -1);        }        catch (StoreException $oException)        {           // fail silent, error is already logged by pooling            return $_aMapLinks;        }        if (!empty($_aEntries))        {            foreach ($_aEntries as $_sEntry)            {                $_aMapLinks[] = MapLink::fromJsonString($_sEntry);            }        }        return $_aMapLinks;    }