namespace Pizza\View\Helper;use Zend\Form\View\Helper\AbstractHelper;use Pizza\Service\PizzaService;class RandomPizza extends AbstractHelper{  protected $service = null;  public function __construct(PizzaService $service)  {    $this->setPizzaService($service);  }  public function setPizzaService(PizzaService $service)  {    $this->service = $service;  }  public function getPizzaService()  {    return $this->service;  }  public function __invoke()  {    $random = $this->getPizzaService()->fetchSingleByRandom();    $html = '<div class="hero-unit">';    $html.= '<h1>' . current($random) . '</h1>';    $html.= '</div>';    return $html;  }}